        {
            if (_world == null || !_world.IsCreated)
            {
                return;
            }

            var rect = new Rect(padding.x, padding.y, 300f, Screen.height);
            GUILayout.BeginArea(rect, GUI.skin.box);

            if (showTime && !_timeQuery.IsEmptyIgnoreFilter)
            {
