        private void EnsureBuffer<T>(Entity entity)
            where T : unmanaged, IBufferElementData
        {
            if (!_entityManager.HasBuffer<T>(entity))
            {
                _entityManager.AddBuffer<T>(entity);
            }
            else
            {
                var buffer = _entityManager.GetBuffer<T>(entity);
                buffer.Clear();
            }
        }

