using UnityEngine;
using UnityEditor;
using UnityEditor.SceneManagement;
using Unity.Scenes;

public class SetupHeadlessBootstrap
{
    public static void Execute()
    {
        var scenePath = "Assets/Scenes/HeadlessBootstrap.unity";
        var scene = EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Single);

        // Create Space4X SubScene
        var space4xObj = new GameObject("Space4X SubScene");
        var space4xSubScene = space4xObj.AddComponent<SubScene>();
        var space4xSceneAsset = AssetDatabase.LoadAssetAtPath<SceneAsset>("Assets/Scenes/Space4XConfig.unity");
        space4xSubScene.SceneAsset = space4xSceneAsset;

        // Create Godgame SubScene (Empty as in original scene)
        var godgameObj = new GameObject("Godgame SubScene");
        var godgameSubScene = godgameObj.AddComponent<SubScene>();

        // Save
        EditorSceneManager.SaveScene(scene);
        Debug.Log("HeadlessBootstrap scene setup complete.");
    }
}

