using UnityEngine;
using UnityEditor;
using UnityEditor.SceneManagement;
using UnityEngine.SceneManagement;

public class SmokeTestHeadlessBootstrap
{
    public static void Execute()
    {
        var scenePath = "Assets/Scenes/HeadlessBootstrap.unity";
        EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Single);

        var rootObjects = SceneManager.GetActiveScene().GetRootGameObjects();
        bool failed = false;

        foreach (var obj in rootObjects)
        {
            if (obj.name == "Main Camera" || obj.name == "Directional Light" || obj.name.Contains("Bootstrap") || obj.name.Contains("Camera"))
            {
                // Allow "HeadlessBootstrap" scene name, but we are checking objects.
                // Allow SubScenes if they contain "Bootstrap" in name? No, SubScenes are usually named "SubScene".
                // But wait, "Space4X SubScene" is allowed.
                // "Godgame SubScene" is allowed.

                if (obj.name.Contains("SubScene")) continue;

                Debug.LogError($"[SmokeTest] Found unexpected object: {obj.name}");
                failed = true;
            }
        }

        if (!failed)
        {
            Debug.Log("[SmokeTest] HeadlessBootstrap scene is clean.");
        }
    }
}

