using Unity.Entities;
using Unity.Mathematics;
using Unity.Transforms;
using Shared.Demo;
using UnityEngine;

namespace Space4X.Demo
{
    public struct TestSpawnTag : IComponentData {}

    [UpdateInGroup(typeof(InitializationSystemGroup))]
    [UpdateAfter(typeof(SharedDemoRenderBootstrap))]
    public partial class TestSpawnSystem : SystemBase
    {
        protected override void OnCreate()
        {
            RequireForUpdate<DemoRenderReady>();
        }

        protected override void OnUpdate()
        {
            if (SystemAPI.HasSingleton<TestSpawnTag>()) return;

            var e = EntityManager.CreateEntity();
            EntityManager.AddComponent<TestSpawnTag>(e);

            // Position (0,0,0), Scale 5-10
            var transform = LocalTransform.FromPosition(float3.zero);
            transform.Scale = 5f;
            EntityManager.AddComponentData(e, transform);

            // Render component
            // Use DemoRenderUtil.MakeRenderable
            // Color doesn't matter, let's use red (1, 0, 0, 1)
            DemoRenderUtil.MakeRenderable(EntityManager, e, new float4(1, 0, 0, 1));

            Debug.Log("[TestSpawnSystem] Spawned test entity at origin.");

            Enabled = false; // Run once
        }
    }
}

using UnityEngine;
using UnityEditor;
using Space4X.Demo;

public class SetupCamera
{
    public static void Execute()
    {
        var camera = GameObject.Find("Main Camera");
        if (camera != null)
        {
            Undo.RecordObject(camera.transform, "Move Camera");
            camera.transform.position = new Vector3(0, 20, -40);
            camera.transform.LookAt(Vector3.zero);

            var controller = camera.GetComponent<DemoCameraController>();
            if (controller != null)
            {
                Undo.RecordObject(controller, "Disable Camera Controller");
                controller.enabled = false;
                Debug.Log("Disabled DemoCameraController.");
            }

            Debug.Log($"Set camera position to {camera.transform.position} and rotation to {camera.transform.rotation.eulerAngles}");
        }
        else
        {
            Debug.LogError("Main Camera not found!");
        }
    }
}

