using Unity.Entities;
using Unity.Rendering;
using Unity.Transforms;
using UnityEngine;
using Space4X.Rendering;
using Space4X.Rendering.Catalog;

namespace Space4X.Rendering.Systems
{
    [WorldSystemFilter(WorldSystemFilterFlags.Default)]
    [UpdateInGroup(typeof(InitializationSystemGroup))]
    public partial struct DebugVerifyVisualsSystem : ISystem
    {
        public void OnCreate(ref SystemState state)
        {
            state.RequireForUpdate<RenderKey>();
            state.RequireForUpdate<Space4XRenderMeshArraySingleton>();
        }

        public void OnUpdate(ref SystemState state)
        {
            var rmaSingleton = SystemAPI.GetSingleton<Space4XRenderMeshArraySingleton>();
            var rma = rmaSingleton.Value;

            int count = 0;
            foreach (var (rk, mmi, entity) in SystemAPI.Query<RefRO<RenderKey>, RefRO<MaterialMeshInfo>>().WithEntityAccess())
            {
                count++;
                if (count == 1)
                {
                    Debug.Log($"[DebugVerifyVisualsSystem] Found entity {entity.Index}:{entity.Version} with RenderKey (ArchetypeId={rk.ValueRO.ArchetypeId}) and MaterialMeshInfo.");

                    if (SystemAPI.HasComponent<LocalTransform>(entity))
                    {
                        var lt = SystemAPI.GetComponent<LocalTransform>(entity);
                        Debug.Log($"[DebugVerifyVisualsSystem] Entity Position: {lt.Position}");
                    }

                    if (SystemAPI.HasComponent<RenderBounds>(entity))
                    {
                        var rb = SystemAPI.GetComponent<RenderBounds>(entity);
                        Debug.Log($"[DebugVerifyVisualsSystem] Entity RenderBounds: Center={rb.Value.Center}, Extents={rb.Value.Extents}");
                    }

                    var index = mmi.ValueRO.MaterialMeshIndex;
                    var mesh = rma.GetMesh(index);
                    var mat = rma.GetMaterial(index);

                    Debug.Log(
                        $"[DebugVerifyVisualsSystem] e={entity} ArchetypeId={rk.ValueRO.ArchetypeId} " +
                        $"Index={index} Mesh={(mesh ? mesh.name : "<null>")} " +
                        $"Mat={(mat ? mat.name : "<null>")}"
                    );
                }
            }

            if (count > 0)
            {
                Debug.Log($"[DebugVerifyVisualsSystem] Found {count} entities with RenderKey + MaterialMeshInfo.");
                state.Enabled = false; // Run once
            }
        }
    }
}
